import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Navbar from './NavBar';
import he from 'he';

function ListingPage() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);

  useEffect(() => {
    fetchListing();
  }, []);

  const fetchListing = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/api/listings/${id}`);
      const decodedDescription = he.decode(response.data.description);
      setListing({ ...response.data, description: decodedDescription });
    } catch (error) {
      console.error('Error fetching listing:', error);
    }
  };  

  if (!listing) {
    return <div>Loading...</div>;
  }

  const { title, images, description, amenities, location, host } = listing;

  return (
    <div>
    <Navbar />
    <div className="p-12">
      {/* Page container */}
      <div className="grid grid-cols-12 gap-4">
        {/* Main content */}
        <div className="col-span-12">
          {/* Title */}
          <h1 className="font-bold text-2xl py-4">{title}</h1>

          {/* Render images */}
          <div className="grid grid-cols-6 gap-4">
            {/* Render first image */}
            {images && (
              <img src={images.data[0].url} alt="" className="w-full h-97 object-cover col-span-3" />
            )}

            {/* Render remaining images */}
            {images && (
              <div className="col-span-3 grid grid-cols-2 gap-4">
                {images.data.slice(1, 5).map((image, index) => (
                  <img
                    key={index}
                    src={image.url}
                    alt=""
                    className="w-full h-48 object-cover"
                  />
                ))}
              </div>
            )}
          </div>

          <div className="grid grid-cols-12 gap-6">
      <div className="col-span-8">
        <div>
          <div>
            <h3 className="font-semibold text-xl py-4">About this place</h3>
            <p style={{ whiteSpace: 'pre-line' }}>{description}</p>
          </div>

          <div>
            <h3>Amenities</h3>
            <ul>
              {amenities.data.map((amenity) => (
                <li key={amenity.id}>{amenity.title}</li>
              ))}
            </ul>
          </div>

          <div>
            <h3>Location</h3>
            <p>{location.address}</p>
            <p>{location.city}</p>
            <p>{location.country.code}</p>
            <p>{location.zip}</p>
          </div>

          <div>
            <h2>{host.name}</h2>
            <img src={host.avatar} alt="Host" />
          </div>
        </div>
      </div>

      {/* Reservation widget */}
      <div className="col-span-4">
        <div className="sticky top-0 h-full py-4 pr-4 border border-gray-300 bg-white rounded-lg">
          <div className="px-4 py-4">
            <h3 className="text-lg font-semibold mb-2">Reservation</h3>
            <div className="mb-4">
              <label htmlFor="check-in">Check-in:</label>
              <input type="date" id="check-in" className="ml-2" />
            </div>
            <div>
              <label htmlFor="check-out">Check-out:</label>
              <input type="date" id="check-out" className="ml-1" />
            </div>
            <div className="flex justify-center mt-4">
              <button className="bg-teal-500 text-white py-2 px-4 rounded-lg">Reserve</button>
            </div>
          </div>
        </div>
      </div>
    </div>
        </div>
      </div>
    </div>
    </div>

  );
}

export default ListingPage;
