import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function ReservationWidget({ listingId }) {
  const [reservationDate, setReservationDate] = useState('');
  const [reservationStatus, setReservationStatus] = useState('');

  const handleReservation = async () => {
    try {
      // Make a reservation request to the server
      const response = await axios.post(`http://localhost:3000/api/reservations`, {
        listingId,
        date: reservationDate,
      });

      setReservationStatus(response.data.status);
    } catch (error) {
      console.error('Error making a reservation:', error);
    }
  };

  return (
    <div>
      <h3>Reservation Widget</h3>
      <input
        type="date"
        value={reservationDate}
        onChange={(e) => setReservationDate(e.target.value)}
      />
      <button onClick={handleReservation}>Make Reservation</button>
      {reservationStatus && <p>{reservationStatus}</p>}
    </div>
  );
}

function ListingPage() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);

  useEffect(() => {
    fetchListing();
  }, []);

  const fetchListing = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/api/listings/${id}`);
      setListing(response.data);
    } catch (error) {
      console.error('Error fetching listing:', error);
    }
  };

  if (!listing) {
    return <div>Loading...</div>;
  }

  const { title, images, description, amenities, location, host } = listing;

  return (
    <div>
      <h1>{title}</h1>

      <div>
        {/* Render images */}
        {images && (
          <div>
            {images.data.map((image, index) => (
              <img key={index} src={image.url} alt={`Image ${index + 1}`} />
            ))}
          </div>
        )}
      </div>

      <div>
        {/* Render host */}
        <h2>{host.name}</h2>
        <img src={host.avatar} alt="Host" />
      </div>

      <div>
        {/* Render description */}
        <h3>Description</h3>
        <p>{description}</p>
      </div>

      <div>
        {/* Render amenities */}
        <h3>Amenities</h3>
        <ul>
          {console.log(amenities.data)}
          {amenities.data.map((amenity, index) => (
            <li key={index}>{amenity}</li>
          ))}
        </ul>
      </div>

      <div>
        {/* Render location */}
        <h3>Location</h3>
        <p>{location.address}</p>
        <p>{location.city}</p>
        <p>{location.country}</p>
        <p>{location.zip}</p>
      </div>

      <ReservationWidget listingId={id} key={id} />
    </div>
  );
}

export default ListingPage;
