import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Navbar from './NavBar';

function ListingPage() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);

  useEffect(() => {
    fetchListing();
  }, []);

  const fetchListing = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/api/listings/${id}`);
      setListing(response.data);
    } catch (error) {
      console.error('Error fetching listing:', error);
    }
  };

  if (!listing) {
    return <div>Loading...</div>;
  }

  const { title, images, description, amenities, location, host } = listing;

  return (
    <div>
      <Navbar />
      <h1 className="font-bold text-2xl">{title}</h1>

      <div className="grid grid-cols-6 gap-4">
        {/* Render first image */}
        {images && (
          <img
            src={images.data[0].url}
            alt={`Image 1`}
            className="w-full h-97 object-cover col-span-3"
          />
        )}

        {/* Render remaining images */}
        {images && (
          <div className="col-span-3 grid grid-cols-2 gap-4">
            {images.data.slice(1, 5).map((image, index) => (
              <img
                key={index}
                src={image.url}
                alt={`Image ${index + 2}`}
                className="w-full h-48 object-cover"
              />
            ))}
          </div>
        )}
      </div>
      <div>
        {/* Render host */}
        <h2>{host.name}</h2>
        <img src={host.avatar} alt="Host" />
      </div>

      <div>
        {/* Render description */}
        <h3>Description</h3>
        <p>{description}</p>
      </div>

      <div>
        {/* Render amenities */}
        <h3>Amenities</h3>
        <ul>
          {amenities.data.map((amenity) => (
            <li key={amenity.id}>{amenity.title}</li>
          ))}
        </ul>
      </div>

      <div>
        {/* Render location */}
        <h3>Location</h3>
        <p>{location.address}</p>
        <p>{location.city}</p>
        <p>{location.country.code}</p>
        <p>{location.zip}</p>
      </div>
    </div>
  );
}

export default ListingPage;
